import React, { Component } from 'react';

class App extends Component {

  constructor(props){
    super(props);
    this.state = {
      range: [],
      divisor: [],
      isLoaded: false
    }
  }

  componentDidMount(){
    fetch('https://join.reckon.com/test1/rangeInfo')
    .then(res => res.json())
    .then(json => {
      console.log(json);
      this.setState({
        range: json,
        isLoaded: true
      })
    })
    .catch((error) => {
      console.error(error);
    });

    fetch('https://join.reckon.com/test1/divisorInfo')
    .then(res => res.json())
    .then(json => {
      console.log(json);      
      this.setState({
        divisor: json.outputDetails,
        isLoaded: true
      })
    })
    .catch((error) => {
      console.error(error);
    });
  }

  render(){

    if(!this.state.isLoaded) {
      return <div>Loading....</div>
    }

    else {         

      // For all numbers inclusive of the lower and upper bound, 
      // go through and check if it divisible by each of the divisors provided in Endpoint2
      // If the number is wholly divisible , log the output as a result.
      // If multiple outputs are satisfied, print outputs that are satisfied.

      //iterate from 0-100 checking if each number is divisible by the divisor
      //e.g. 3 is divisible by the divisor of 3
      //while number is between 0 & 100, count++, start at 0
      var items = [];
      var number = 0; 

      this.state.divisor.forEach(populateArray());

      function populateArray(value, index, array){
        while(number => this.state.range.lower && number <= this.state.range.upper){
          console.log(number); 
          if(number % value === 0){ //is divisible
             items[number] = value;
          }
          number++;
        }
      }

      return (
        <div className="App">

          <ul>
            <li>
              {this.state.range.lower}
              {this.state.range.upper}
            </li>
          </ul>

          <ul>
            {items.map((item, key)=>{
               return<li key={key}>{item}</li>
            })}
          </ul>
        </div>
      );
    }
  }
}

export default App;
