import React, { Component } from 'react';

class App extends Component {

  constructor(props){
    super(props);
    this.state = {
      range: [],
      divisor: [],
      isLoaded: false
    }
  }

  componentDidMount(){
    fetch('https://join.reckon.com/test1/rangeInfo')
    .then(res => res.json())
    .then(json => {
      this.setState({
        isLoaded: true,
        range: json,
      })
    });

    fetch('https://join.reckon.com/test1/divisorInfo')
    .then(res => res.json())
    .then(json => {
      this.setState({
        isLoaded: true,
        divisor: json,
      })
    });
  }

  render() {

    var { isLoaded, range, divisor } = this.state;

    if(!isLoaded) {
      return <div>Loading....</div>
    }

    else {
      const data = Array.from(divisor);      
      const listItems = data.map((d) => <li key={d.outputDetails}>{d.divisor}|{d.output}</li>);      

      return (
        <div className="App">
          <ul>
            <li>{range.lower}</li>
            <li>{range.upper}</li>
          </ul>

          <ul>
            {data.outputDetails.divisor}
          </ul>

        </div>
      );
    }
  }
}

export default App;
