import React, { Component } from 'react';

class App extends Component {

  constructor(props){
    super(props);
    this.state = {
      range: [],
      divisor: [],
      isLoaded: false
    }
  }

  componentDidMount(){
    fetch('https://join.reckon.com/test1/rangeInfo')
    .then(res => res.json())
    .then(json => {
      console.log(json);
      this.setState({
        range: json,
        isLoaded: true
      })
    })
    .catch((error) => {
      console.error(error);
    });

    fetch('https://join.reckon.com/test1/divisorInfo')
    .then(res => res.json())
    .then(json => {
      console.log(json);      
      this.setState({
        divisor: json.outputDetails,
        isLoaded: true
      })
    })
    .catch((error) => {
      console.error(error);
    });
  }

  render(){

    if(!this.state.isLoaded) {
      return <div>Loading....</div>
    }

    else {         
      //var {isLoaded, range, divisor} = this.state;

      return (
        <div className="App">

          <ul>
            <li>
              {this.state.range.lower}
              {this.state.range.upper}
            </li>
          </ul>

          <ul>
            {this.state.divisor.map((item, key)=> {
              return <li key={key}>{item.divisor} {item.output}</li>
            })}
          </ul>
        </div>
      );
    }
  }
}

export default App;
