import React, { Component } from 'react';

class App extends Component {

  constructor(props){
    super(props);
    this.state = {
      range: [],
      divisor: [],
      isLoaded: false
    }
  }

  componentDidMount(){
    fetch('https://join.reckon.com/test1/rangeInfo')
    .then(res => res.json())
    .then(json => {
      this.setState({
        range: json,
      })
    });

    fetch('https://join.reckon.com/test1/divisorInfo')
    .then(res => res.json())
    .then(json => {
      this.setState({
        divisor: json,
      })
    });

    this.setState({
      isLoaded: true
    });
  }

  render(){

    var { isLoaded, range, divisor } = this.state;

    if(!isLoaded) {
      return <div>Loading....</div>
    }

    else {
      console.log('loaded');
      //returns an empty array?
      //divisor = Array.from(divisor); 
      //range = Array.from(range);   

      var {isLoaded, range, divisor} = this.state;

      return (
        <div className="App">
          <ul>
            <li>
              {range.lower}
              {range.upper}
            </li>
          </ul>

          <ul>
            {divisor.map((item, key)=> {
              return <li key={key}>{item.divisor}</li>
            })}
          </ul>
        </div>
      );
    }
  }
}

export default App;
